import React, { useState, useRef, useEffect } from 'react';
import { Box, Typography, Grid, CircularProgress, Alert, Paper } from '@mui/material';
import { FaceRetouchingOff, Fingerprint, Keyboard, Mouse } from '@mui/icons-material';
import { useNavigate } from 'react-router-dom';
import { authService } from '../services/authService';
import { behaviorService } from '../services/behaviorService';
import '../styles/neumorphic.css';

const AuthForm = ({ isRegister = false }) => {
  const [formState, setFormState] = useState({
    username: '',
    password: '',
    email: '',
    full_name: '',
    step: 'credentials', // credentials -> biometric -> behavioral -> complete
    loading: false,
    error: null,
    biometricType: 'face', // face, fingerprint
    behavioralMonitoring: false,
    userId: null,
    token: null
  });

  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const navigate = useNavigate();

  // Handle form input changes
  const handleChange = (e) => {
    setFormState({
      ...formState,
      [e.target.name]: e.target.value
    });
  };

  // Handle credential submission
  const handleCredentialSubmit = async (e) => {
    e.preventDefault();
    setFormState({ ...formState, loading: true, error: null });

    try {
      let response;

      if (isRegister) {
        response = await authService.register({
          username: formState.username,
          password: formState.password,
          email: formState.email,
          full_name: formState.full_name
        });
        setFormState({
          ...formState,
          loading: false,
          step: 'biometric',
          userId: response.userId,
          token: response.token
        });
      } else {
        response = await authService.login(formState.username, formState.password);
        setFormState({
          ...formState,
          loading: false,
          step: 'biometric',
          userId: response.userId,
          token: response.token
        });
      }
    } catch (error) {
      setFormState({
        ...formState,
        loading: false,
        error: error.message || (isRegister ? 'Registration failed. Please try again.' : 'Authentication failed. Please try again.')
      });
    }
  };

  // Handle biometric authentication
  const handleBiometricAuth = async () => {
    setFormState({ ...formState, loading: true, error: null });

    try {
      let biometricData;

      if (formState.biometricType === 'face') {
        biometricData = await captureFace();
      } else {
        biometricData = await captureFingerprint();
      }

      const response = await authService.verifyBiometric(
        formState.userId,
        formState.token,
        formState.biometricType,
        biometricData
      );

      setFormState({
        ...formState,
        loading: false,
        step: 'behavioral',
        token: response.token
      });

      // Start behavioral monitoring
      startBehavioralMonitoring();

    } catch (error) {
      setFormState({
        ...formState,
        loading: false,
        error: error.message || 'Biometric verification failed. Please try again.'
      });
    }
  };

  // Capture face image from webcam
  const captureFace = () => {
    return new Promise((resolve, reject) => {
      try {
        const video = videoRef.current;
        const canvas = canvasRef.current;
        const context = canvas.getContext('2d');

        // Draw the current video frame to the canvas
        context.drawImage(video, 0, 0, canvas.width, canvas.height);

        // Get the image data as base64 string
        const imageData = canvas.toDataURL('image/jpeg');
        resolve(imageData);
      } catch (error) {
        reject(error);
      }
    });
  };

  // Simulate fingerprint capture
  const captureFingerprint = () => {
    return new Promise((resolve) => {
      // In a real app, this would interface with a fingerprint scanner
      // For demo purposes, we'll just simulate a delay and return mock data
      setTimeout(() => {
        resolve('mock-fingerprint-data');
      }, 1500);
    });
  };

  // Start behavioral monitoring
  const startBehavioralMonitoring = () => {
    setFormState({ ...formState, behavioralMonitoring: true });

    // Start keystroke monitoring
    behaviorService.startKeystrokeMonitoring(formState.userId, formState.token);

    // Start mouse monitoring
    behaviorService.startMouseMonitoring(formState.userId, formState.token);

    // Start device monitoring
    behaviorService.startDeviceMonitoring(formState.userId, formState.token);

    // After a short period, complete the authentication process
    setTimeout(() => {
      setFormState({
        ...formState,
        step: 'complete',
        behavioralMonitoring: false
      });

      // Store authentication token and redirect to dashboard
      localStorage.setItem('authToken', formState.token);
      localStorage.setItem('userId', formState.userId);
      navigate('/dashboard');
    }, 3000);
  };

  // Initialize webcam when in biometric step and using face authentication
  useEffect(() => {
    if (formState.step === 'biometric' && formState.biometricType === 'face') {
      const startWebcam = async () => {
        try {
          const stream = await navigator.mediaDevices.getUserMedia({ video: true });
          if (videoRef.current) {
            videoRef.current.srcObject = stream;
          }
        } catch (error) {
          console.error('Error accessing webcam:', error);
          setFormState({
            ...formState,
            error: 'Could not access webcam. Please check permissions.'
          });
        }
      };

      startWebcam();

      // Cleanup function to stop webcam when component unmounts
      return () => {
        if (videoRef.current && videoRef.current.srcObject) {
          videoRef.current.srcObject.getTracks().forEach(track => track.stop());
        }
      };
    }
  }, [formState.step, formState.biometricType]);

  // Render different form steps
  const renderFormStep = () => {
    switch (formState.step) {
      case 'credentials':
        return (
          <form onSubmit={handleCredentialSubmit}>
            <Typography variant="h5" gutterBottom>
              {isRegister ? 'Create Account' : 'Login'}
            </Typography>

            {isRegister && (
              <div className="neumorphic-input-container">
                <label className="neumorphic-label">Full Name</label>
                <input
                  className="neumorphic-input"
                  type="text"
                  name="full_name"
                  value={formState.full_name}
                  onChange={handleChange}
                  required
                  placeholder="Enter your full name"
                />
              </div>
            )}

            <div className="neumorphic-input-container">
              <label className="neumorphic-label">Username</label>
              <input
                className="neumorphic-input"
                type="text"
                name="username"
                value={formState.username}
                onChange={handleChange}
                required
                placeholder="Enter your username"
              />
            </div>

            {isRegister && (
              <div className="neumorphic-input-container">
                <label className="neumorphic-label">Email</label>
                <input
                  className="neumorphic-input"
                  type="email"
                  name="email"
                  value={formState.email}
                  onChange={handleChange}
                  required
                  placeholder="Enter your email address"
                />
              </div>
            )}
            <div className="neumorphic-input-container">
              <label className="neumorphic-label">Password</label>
              <input
                className="neumorphic-input"
                type="password"
                name="password"
                value={formState.password}
                onChange={handleChange}
                required
                placeholder="Enter your password"
              />
            </div>
            <button
              type="submit"
              className="neumorphic-button primary"
              disabled={formState.loading}
              style={{ width: '100%', marginTop: '24px', marginBottom: '16px' }}
            >
              {formState.loading ? <CircularProgress size={24} /> : 'Next'}
            </button>
          </form>
        );

      case 'biometric':
        return (
          <Box>
            <Typography variant="h5" gutterBottom>
              Biometric Verification
            </Typography>
            <Typography variant="body1" gutterBottom>
              Please verify your identity using {formState.biometricType === 'face' ? 'facial recognition' : 'fingerprint'}.
            </Typography>

            <Box sx={{ display: 'flex', justifyContent: 'center', mb: 2 }}>
              <div 
                className={`neumorphic-card interactive ${formState.biometricType === 'face' ? 'active' : ''}`}
                style={{ padding: '10px 20px', margin: '0 8px', cursor: 'pointer', display: 'flex', alignItems: 'center' }}
                onClick={() => setFormState({ ...formState, biometricType: 'face' })}
              >
                <FaceRetouchingOff style={{ marginRight: '8px' }} />
                Face
              </div>
              <div 
                className={`neumorphic-card interactive ${formState.biometricType === 'fingerprint' ? 'active' : ''}`}
                style={{ padding: '10px 20px', margin: '0 8px', cursor: 'pointer', display: 'flex', alignItems: 'center' }}
                onClick={() => setFormState({ ...formState, biometricType: 'fingerprint' })}
              >
                <Fingerprint style={{ marginRight: '8px' }} />
                Fingerprint
              </div>
            </Box>

            {formState.biometricType === 'face' ? (
              <div className="neumorphic-card" style={{ position: 'relative', width: '100%', height: 300, marginBottom: '16px', overflow: 'hidden' }}>
                <video
                  ref={videoRef}
                  autoPlay
                  playsInline
                  style={{ width: '100%', height: '100%', objectFit: 'cover' }}
                />
                <canvas
                  ref={canvasRef}
                  width="640"
                  height="480"
                  style={{ display: 'none' }}
                />
              </div>
            ) : (
              <div className="neumorphic-card" style={{ display: 'flex', justifyContent: 'center', padding: '40px', marginBottom: '16px' }}>
                <Fingerprint sx={{ fontSize: 120, color: 'var(--primary-color)' }} />
              </div>
            )}

            <button
              className="neumorphic-button primary"
              onClick={handleBiometricAuth}
              disabled={formState.loading}
              style={{ width: '100%' }}
            >
              {formState.loading ? <CircularProgress size={24} /> : 'Verify'}
            </button>
          </Box>
        );

      case 'behavioral':
        return (
          <Box>
            <Typography variant="h5" gutterBottom>
              Behavioral Monitoring
            </Typography>
            <Typography variant="body1" gutterBottom>
              Setting up continuous authentication...
            </Typography>

            <Box sx={{ display: 'flex', justifyContent: 'center', my: 4 }}>
              <CircularProgress />
            </Box>

            <Grid container spacing={2} sx={{ mt: 2 }}>
              <Grid item xs={6}>
                <Paper elevation={2} sx={{ p: 2, textAlign: 'center' }}>
                  <Keyboard sx={{ fontSize: 40, color: 'primary.main' }} />
                  <Typography variant="body2">
                    Keystroke Dynamics
                  </Typography>
                </Paper>
              </Grid>
              <Grid item xs={6}>
                <Paper elevation={2} sx={{ p: 2, textAlign: 'center' }}>
                  <Mouse sx={{ fontSize: 40, color: 'primary.main' }} />
                  <Typography variant="body2">
                    Mouse Movements
                  </Typography>
                </Paper>
              </Grid>
            </Grid>
          </Box>
        );

      case 'complete':
        return (
          <Box>
            <Typography variant="h5" gutterBottom>
              Authentication Complete
            </Typography>
            <Typography variant="body1" gutterBottom>
              You have been successfully authenticated.
            </Typography>
            <Typography variant="body2" color="textSecondary">
              Redirecting to dashboard...
            </Typography>
          </Box>
        );

      default:
        return null;
    }
  };

  return (
    <Paper elevation={3} sx={{ p: 4, maxWidth: 500, mx: 'auto', mt: 8 }}>
      {formState.error && (
        <Alert severity="error" sx={{ mb: 2 }}>
          {formState.error}
        </Alert>
      )}
      {renderFormStep()}
    </Paper>
  );
};

export default AuthForm;
