import React, { useState, useRef, useEffect } from 'react';
import { Box, Typography, Grid, CircularProgress, Alert, Paper, Button, TextField, Card, CardContent, Fade, Slide } from '@mui/material';
import { FaceRetouchingOff, Fingerprint, Keyboard, Mouse, Visibility, VisibilityOff, Person, Email, Lock } from '@mui/icons-material';
import { useNavigate } from 'react-router-dom';
import { authService } from '../services/authService';
import { behaviorService } from '../services/behaviorService';
import '../styles/modern-auth.css';

const AuthForm = ({ isRegister = false }) => {
  const [formState, setFormState] = useState({
    username: '',
    password: '',
    email: '',
    full_name: '',
    step: 'credentials', // credentials -> biometric -> behavioral -> complete
    loading: false,
    error: null,
    biometricType: 'face', // face, fingerprint
    behavioralMonitoring: false,
    userId: null,
    token: null,
    showPassword: false
  });

  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const navigate = useNavigate();

  // Handle form input changes
  const handleChange = (e) => {
    setFormState({
      ...formState,
      [e.target.name]: e.target.value,
      error: null // Clear error when user starts typing
    });
  };

  // Handle credential submission
  const handleCredentialSubmit = async (e) => {
    e.preventDefault();
    setFormState({ ...formState, loading: true, error: null });

    try {
      let response;

      if (isRegister) {
        response = await authService.register({
          username: formState.username,
          password: formState.password,
          email: formState.email,
          full_name: formState.full_name
        });
        setFormState({
          ...formState,
          loading: false,
          step: 'biometric',
          userId: response.userId,
          token: response.token
        });
      } else {
        response = await authService.login(formState.username, formState.password);
        setFormState({
          ...formState,
          loading: false,
          step: 'biometric',
          userId: response.userId,
          token: response.token
        });
      }
    } catch (error) {
      setFormState({
        ...formState,
        loading: false,
        error: error.message || (isRegister ? 'Registration failed. Please try again.' : 'Authentication failed. Please try again.')
      });
    }
  };

  // Handle biometric authentication
  const handleBiometricAuth = async () => {
    setFormState({ ...formState, loading: true, error: null });

    try {
      let biometricData;

      if (formState.biometricType === 'face') {
        biometricData = await captureFace();
      } else {
        biometricData = await captureFingerprint();
      }

      const response = await authService.verifyBiometric(
        formState.userId,
        formState.token,
        formState.biometricType,
        biometricData
      );

      setFormState({
        ...formState,
        loading: false,
        step: 'behavioral',
        token: response.token
      });

      // Start behavioral monitoring
      startBehavioralMonitoring();

    } catch (error) {
      setFormState({
        ...formState,
        loading: false,
        error: error.message || 'Biometric verification failed. Please try again.'
      });
    }
  };

  // Capture face image from webcam
  const captureFace = () => {
    return new Promise((resolve, reject) => {
      try {
        const video = videoRef.current;
        const canvas = canvasRef.current;
        const context = canvas.getContext('2d');

        // Draw the current video frame to the canvas
        context.drawImage(video, 0, 0, canvas.width, canvas.height);

        // Get the image data as base64 string
        const imageData = canvas.toDataURL('image/jpeg');
        resolve(imageData);
      } catch (error) {
        reject(error);
      }
    });
  };

  // Simulate fingerprint capture
  const captureFingerprint = () => {
    return new Promise((resolve) => {
      // In a real app, this would interface with a fingerprint scanner
      // For demo purposes, we'll just simulate a delay and return mock data
      setTimeout(() => {
        resolve('mock-fingerprint-data');
      }, 1500);
    });
  };

  // Start behavioral monitoring
  const startBehavioralMonitoring = () => {
    setFormState({ ...formState, behavioralMonitoring: true });

    // Start keystroke monitoring
    behaviorService.startKeystrokeMonitoring(formState.userId, formState.token);

    // Start mouse monitoring
    behaviorService.startMouseMonitoring(formState.userId, formState.token);

    // Start device monitoring
    behaviorService.startDeviceMonitoring(formState.userId, formState.token);

    // After a short period, complete the authentication process
    setTimeout(() => {
      setFormState({
        ...formState,
        step: 'complete',
        behavioralMonitoring: false
      });

      // Store authentication token and redirect to dashboard
      localStorage.setItem('authToken', formState.token);
      localStorage.setItem('userId', formState.userId);
      navigate('/dashboard');
    }, 3000);
  };

  // Initialize webcam when in biometric step and using face authentication
  useEffect(() => {
    if (formState.step === 'biometric' && formState.biometricType === 'face') {
      const startWebcam = async () => {
        try {
          const stream = await navigator.mediaDevices.getUserMedia({ video: true });
          if (videoRef.current) {
            videoRef.current.srcObject = stream;
          }
        } catch (error) {
          console.error('Error accessing webcam:', error);
          setFormState({
            ...formState,
            error: 'Could not access webcam. Please check permissions.'
          });
        }
      };

      startWebcam();

      // Cleanup function to stop webcam when component unmounts
      return () => {
        if (videoRef.current && videoRef.current.srcObject) {
          videoRef.current.srcObject.getTracks().forEach(track => track.stop());
        }
      };
    }
  }, [formState.step, formState.biometricType]);

  // Render different form steps
  const renderFormStep = () => {
    switch (formState.step) {
      case 'credentials':
        return (
          <Fade in={true} timeout={800}>
            <Card className="modern-auth-card" elevation={0}>
              <CardContent sx={{ p: 4 }}>
                <Box className="auth-header" sx={{ textAlign: 'center', mb: 4 }}>
                  <Typography variant="h4" className="auth-title" gutterBottom>
                    {isRegister ? 'Create Account' : 'Welcome Back'}
                  </Typography>
                  <Typography variant="body1" className="auth-subtitle">
                    {isRegister ? 'Join our secure biometric platform' : 'Sign in to your account'}
                  </Typography>
                </Box>

                <form onSubmit={handleCredentialSubmit} className="auth-form">
                  {isRegister && (
                    <Box className="input-group" sx={{ mb: 3 }}>
                      <TextField
                        fullWidth
                        variant="outlined"
                        label="Full Name"
                        name="full_name"
                        value={formState.full_name}
                        onChange={handleChange}
                        required
                        className="modern-input"
                        InputProps={{
                          startAdornment: <Person sx={{ color: 'action.active', mr: 1 }} />
                        }}
                      />
                    </Box>
                  )}

                  <Box className="input-group" sx={{ mb: 3 }}>
                    <TextField
                      fullWidth
                      variant="outlined"
                      label="Username"
                      name="username"
                      value={formState.username}
                      onChange={handleChange}
                      required
                      className="modern-input"
                      InputProps={{
                        startAdornment: <Person sx={{ color: 'action.active', mr: 1 }} />
                      }}
                    />
                  </Box>

                  {isRegister && (
                    <Box className="input-group" sx={{ mb: 3 }}>
                      <TextField
                        fullWidth
                        variant="outlined"
                        label="Email"
                        name="email"
                        type="email"
                        value={formState.email}
                        onChange={handleChange}
                        required
                        className="modern-input"
                        InputProps={{
                          startAdornment: <Email sx={{ color: 'action.active', mr: 1 }} />
                        }}
                      />
                    </Box>
                  )}

                  <Box className="input-group" sx={{ mb: 4 }}>
                    <TextField
                      fullWidth
                      variant="outlined"
                      label="Password"
                      name="password"
                      type={formState.showPassword ? 'text' : 'password'}
                      value={formState.password}
                      onChange={handleChange}
                      required
                      className="modern-input"
                      InputProps={{
                        startAdornment: <Lock sx={{ color: 'action.active', mr: 1 }} />,
                        endAdornment: (
                          <Box
                            sx={{ cursor: 'pointer' }}
                            onClick={() => setFormState({ ...formState, showPassword: !formState.showPassword })}
                          >
                            {formState.showPassword ? <VisibilityOff /> : <Visibility />}
                          </Box>
                        )
                      }}
                    />
                  </Box>

                  <Button
                    type="submit"
                    fullWidth
                    variant="contained"
                    disabled={formState.loading}
                    className="modern-auth-button"
                    sx={{ py: 1.5, fontSize: '1.1rem' }}
                  >
                    {formState.loading ? (
                      <CircularProgress size={24} color="inherit" />
                    ) : (
                      isRegister ? 'Create Account' : 'Sign In'
                    )}
                  </Button>
                </form>
              </CardContent>
            </Card>
          </Fade>
        );

      case 'biometric':
        return (
          <Slide direction="left" in={true} timeout={600}>
            <Card className="modern-auth-card biometric-card" elevation={0}>
              <CardContent sx={{ p: 4 }}>
                <Box className="auth-header" sx={{ textAlign: 'center', mb: 4 }}>
                  <Typography variant="h4" className="auth-title" gutterBottom>
                    Biometric Verification
                  </Typography>
                  <Typography variant="body1" className="auth-subtitle">
                    Secure your account with {formState.biometricType === 'face' ? 'facial recognition' : 'fingerprint authentication'}
                  </Typography>
                </Box>

                <Box className="biometric-selector" sx={{ display: 'flex', justifyContent: 'center', mb: 4, gap: 2 }}>
                  <Button
                    variant={formState.biometricType === 'face' ? 'contained' : 'outlined'}
                    className={`biometric-option ${formState.biometricType === 'face' ? 'active' : ''}`}
                    onClick={() => setFormState({ ...formState, biometricType: 'face' })}
                    startIcon={<FaceRetouchingOff />}
                    sx={{ px: 3, py: 1.5 }}
                  >
                    Face ID
                  </Button>
                  <Button
                    variant={formState.biometricType === 'fingerprint' ? 'contained' : 'outlined'}
                    className={`biometric-option ${formState.biometricType === 'fingerprint' ? 'active' : ''}`}
                    onClick={() => setFormState({ ...formState, biometricType: 'fingerprint' })}
                    startIcon={<Fingerprint />}
                    sx={{ px: 3, py: 1.5 }}
                  >
                    Fingerprint
                  </Button>
                </Box>

                <Box className="biometric-capture" sx={{ mb: 4 }}>
                  {formState.biometricType === 'face' ? (
                    <Card className="camera-container" sx={{ position: 'relative', height: 300, overflow: 'hidden', borderRadius: 3 }}>
                      <video
                        ref={videoRef}
                        autoPlay
                        playsInline
                        className="camera-feed"
                        style={{ width: '100%', height: '100%', objectFit: 'cover' }}
                      />
                      <canvas
                        ref={canvasRef}
                        width="640"
                        height="480"
                        style={{ display: 'none' }}
                      />
                      <Box className="camera-overlay" sx={{
                        position: 'absolute',
                        top: '50%',
                        left: '50%',
                        transform: 'translate(-50%, -50%)',
                        width: 200,
                        height: 200,
                        border: '3px solid rgba(255,255,255,0.8)',
                        borderRadius: '50%',
                        pointerEvents: 'none'
                      }} />
                    </Card>
                  ) : (
                    <Card className="fingerprint-container" sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: 300, borderRadius: 3 }}>
                      <Box sx={{ textAlign: 'center' }}>
                        <Fingerprint sx={{ fontSize: 120, color: 'primary.main', mb: 2 }} />
                        <Typography variant="body1" color="textSecondary">
                          Place your finger on the sensor
                        </Typography>
                      </Box>
                    </Card>
                  )}
                </Box>

                <Button
                  fullWidth
                  variant="contained"
                  onClick={handleBiometricAuth}
                  disabled={formState.loading}
                  className="modern-auth-button"
                  sx={{ py: 1.5, fontSize: '1.1rem' }}
                >
                  {formState.loading ? (
                    <CircularProgress size={24} color="inherit" />
                  ) : (
                    'Verify Identity'
                  )}
                </Button>
              </CardContent>
            </Card>
          </Slide>
        );

      case 'behavioral':
        return (
          <Fade in={true} timeout={800}>
            <Card className="modern-auth-card behavioral-card" elevation={0}>
              <CardContent sx={{ p: 4 }}>
                <Box className="auth-header" sx={{ textAlign: 'center', mb: 4 }}>
                  <Typography variant="h4" className="auth-title" gutterBottom>
                    Behavioral Analysis
                  </Typography>
                  <Typography variant="body1" className="auth-subtitle">
                    Setting up continuous authentication patterns...
                  </Typography>
                </Box>

                <Box sx={{ display: 'flex', justifyContent: 'center', my: 4 }}>
                  <CircularProgress size={60} thickness={4} className="loading-spinner" />
                </Box>

                <Grid container spacing={3} sx={{ mt: 2 }}>
                  <Grid item xs={6}>
                    <Card className="monitoring-card" elevation={0} sx={{ p: 3, textAlign: 'center', borderRadius: 3 }}>
                      <Keyboard sx={{ fontSize: 48, color: 'primary.main', mb: 2 }} />
                      <Typography variant="h6" gutterBottom>
                        Keystroke
                      </Typography>
                      <Typography variant="body2" color="textSecondary">
                        Analyzing typing patterns
                      </Typography>
                    </Card>
                  </Grid>
                  <Grid item xs={6}>
                    <Card className="monitoring-card" elevation={0} sx={{ p: 3, textAlign: 'center', borderRadius: 3 }}>
                      <Mouse sx={{ fontSize: 48, color: 'primary.main', mb: 2 }} />
                      <Typography variant="h6" gutterBottom>
                        Mouse
                      </Typography>
                      <Typography variant="body2" color="textSecondary">
                        Learning movement patterns
                      </Typography>
                    </Card>
                  </Grid>
                </Grid>
              </CardContent>
            </Card>
          </Fade>
        );

      case 'complete':
        return (
          <Fade in={true} timeout={1000}>
            <Card className="modern-auth-card success-card" elevation={0}>
              <CardContent sx={{ p: 4, textAlign: 'center' }}>
                <Box className="success-animation" sx={{ mb: 4 }}>
                  <Box className="checkmark-circle">
                    <Box className="checkmark" />
                  </Box>
                </Box>
                <Typography variant="h4" className="auth-title success-title" gutterBottom>
                  Welcome!
                </Typography>
                <Typography variant="body1" className="auth-subtitle" sx={{ mb: 3 }}>
                  Authentication completed successfully
                </Typography>
                <Typography variant="body2" color="textSecondary">
                  Redirecting to your secure dashboard...
                </Typography>
              </CardContent>
            </Card>
          </Fade>
        );

      default:
        return null;
    }
  };

  return (
    <Box className="modern-auth-container" sx={{ minHeight: '100vh', display: 'flex', alignItems: 'center', justifyContent: 'center', p: 2 }}>
      <Box className="auth-wrapper" sx={{ width: '100%', maxWidth: 480 }}>
        {formState.error && (
          <Fade in={true}>
            <Alert severity="error" sx={{ mb: 3, borderRadius: 2 }}>
              {formState.error}
            </Alert>
          </Fade>
        )}
        {renderFormStep()}
      </Box>
    </Box>
  );
};

export default AuthForm;
